mapper.select:
select humanno,humanid,humanpwd ,humanauthority
from human_resource_management where 1=1

	and humanid = #{humanId}


	and humanno = #{humanNo}

select hrm.humanno, hrm.humankorname, hrm.humanresidentnumber, hdc.departmentname, hr.rankname, hrm.humanenteringdate, hrm.humanaccountnumber, hrm.humanemail from human_resource_management as hrm, human_department_code as hdc, human_rank as hr where hrm.departmentcodeno = hdc.departmentcodeno and hrm.rankno = hr.rankno
and hrm.humanno = #{humanNo}
and hrm.humankorname = #{humanKorName}
order by humanno desc
